# Process Stage 3 Entity
# Anonymize fields indicated in the canvas_entity_columns_to_anonymize array
# Save in stage 3 container

def processStage3Entity(entityName, entityColumns):
    df = spark.read.format('parquet').load(stage2 + f'/canvas/{entityName}')
    if (df.count() > 0):
        for column_to_anonymize in canvas_entity_columns_to_anonymize:
            if (column_to_anonymize in df.columns):
                df.withColumn(column_to_anonymize, lit('*'))   
        df.write.format("parquet").mode("overwrite").save(stage3 + f'/canvas/{entityName}')

def previewStage3Entity(entityName, entityColumns):
    df = spark.read.format('parquet').load(stage3 + f'/canvas/{entityName}')
    if (df.count() > 0):
        df.show()
