def ingestResource(courseId, resource):
    headers = getRequestHeaders()
    apiUrl = f"https://classroom.googleapis.com/v1/courses/{courseId}/{resource}"
    r = requests.get(apiUrl, headers=headers)
    responseJson = json.loads(r.text)

    if (len(responseJson) == 0):
        print(f"No data found for resource: {resource}")
        return
    
    resourcesJson = json.dumps(responseJson[resource]);
    resourceDf = spark.read.json(sc.parallelize([resourcesJson]))

    if (not 'courseId' in resourceDf.columns):
        resourceDf = resourceDf.withColumn('courseId', lit(courseId))

    resourceDf.write.format('json').mode('append').save(stage1 + f'/googleclassroom/{resource}')

