def getRequestHeaders():
    return {
        "Authorization" : bearerToken, 
        "Accept-Encoding": "gzip", 
        #User-Agent provided based on google performance docs - https://developers.google.com/classroom/guides/performance
        "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/90.0.4430.93 Safari/537.36"

def ingestResource(courseId, resource):
    r = requests.get(f"{googleClassroomBaseUrl}{googleClassroomRelativeUrl}/{courseId}/{resource}", headers=headers)
    resourcesJson = json.dumps(json.loads(r.text)[resource]);
    resourceDf = spark.read.json(sc.parallelize([resourcesJson]))
    resourceDf.write.format('json').mode('overwrite').save(stage1 + f'/googleclassroom/{resource}')

# Iterate through Courses to fetch course related data
for i in coursesDf.rdd.toLocalIterator():
    processCourseRow(i)
